import React, { useEffect, useState } from 'react';
import axios from 'axios';
import "../App.css";
import Footer from '../components/Footer';
import Header from '../components/Header';

const Catalog = () => {
  const [media, setProducts] = useState([]);

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const response = await axios.get('http://localhost:3000/media');
        setProducts(response.data);
      } catch (error) {
        console.error('Ошибка при загрузке товаров:', error);
      }
    };

    fetchProducts();
  }, []);

  return (
    <div className="catalog">
      <Header/>
      <h2>Каталог товаров</h2>
      <div className="product-list">
        {media.map(media => (
              <div key={media.id} className="product-card">
                <img src={media.image} alt={media.name} className="product-image" />
                <h3>{media.name}</h3>
                <p>Цена: ${media.price}</p>
              </div>
            )
          )
        }
      </div>
      <Footer/>
    </div>
  );
};

export default Catalog;
