// src/Cards.js
import React from 'react';
import '../App.css';

const categories = [
  {
    title: 'Компьютеры',
    imageUrl: '/path/to/computers-image.jpg', // replace with your image path
    bgColor: '#ffe6f2',
    link: '/computers'
  },
  {
    title: 'Ноутбуки',
    imageUrl: '/path/to/laptops-image.jpg', // replace with your image path
    bgColor: '#f0f0f0',
    link: '/laptops'
  },
  {
    title: 'Мониторы',
    imageUrl: '/path/to/monitors-image.jpg', // replace with your image path
    bgColor: '#ffe6cc',
    link: '/monitors'
  },
  {
    title: 'Периферия',
    imageUrl: '/path/to/peripherals-image.jpg', // replace with your image path
    bgColor: '#e6f2ff',
    link: '/peripherals'
  },
];

const Cards = () => {
  return (
    <div className="categories">
      {categories.map((category, index) => (
          <div
            className="category-card"
            style={{ backgroundColor: category.bgColor }}
          >
            <img src={category.imageUrl} alt={category.title} className="category-image" />
            <div className="category-info">
              <h2>{category.title}</h2>
              <span className="arrow">→</span>
            </div>
          </div>
      ))}
    </div>
  );
};

export default Cards;
