import axios from 'axios';
import React, { useState } from 'react';
import { useForm } from "react-hook-form";
import { Link } from 'react-router-dom';
import '../App.css'; // убедитесь, что вы подключили CSS

const TestForm = () => {
    const [inputData, setInputData] = useState({name: '', description: '', url: ''});
    const { handleSubmit } = useForm();

    const onSubmit = (data) => {
        axios.post('http://localhost:3001/media', data)
            .then(res => {
                alert("Data sent successfully");
            })
            .catch(err => {
                alert("Error sending data");
            });
    };

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setInputData({ ...inputData, [name]: value });
    };

    return (
        <div>
            <header className="header">
                <div className="logo">
                    <span className="logo-text">x64</span>
                </div>
                <nav className="nav">
                    <div className="search">
                        <input type="text" placeholder="Поиск" />
                    </div>
                </nav>
                <div className="user-actions">
                    <Link className="icon-button" to="/Profile">👤</Link>
                    <Link className="icon-button" to="/Statistics">📊</Link>
                    <Link className="icon-button" to="/Favorites">❤️</Link>
                    <Link className="icon-button" to="/Cart">🛒</Link>
                </div>
            </header>

            <main>
                <h2>Форма добавления медиа</h2>
                <form onSubmit={handleSubmit(onSubmit)}>
                    <div>
                        <label htmlFor="name">Название:</label>
                        <input
                            type="text"
                            id="name"
                            name="name"
                            value={inputData.name}
                            onChange={handleInputChange}
                        />
                    </div>
                    <div>
                        <label htmlFor="description">Описание:</label>
                        <input
                            type="text"
                            id="description"
                            name="description"
                            value={inputData.description}
                            onChange={handleInputChange}
                        />
                    </div>
                    <div>
                        <label htmlFor="url">URL:</label>
                        <input
                            type="text"
                            id="url"
                            name="url"
                            value={inputData.url}
                            onChange={handleInputChange}
                        />
                    </div>
                    <button type="submit">Отправить</button>
                </form>
            </main>
        </div>
    );
};

export default TestForm;
