import React, { useEffect, useState } from 'react';
import axios from 'axios';
import "../App.css";
import Footer from '../components/Footer';
import Header from '../components/Header';

const Catalog = () => {
  const [media, setProducts] = useState([]);

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const response = await axios.get('http://localhost:3000/Catalog');
        setProducts(response.data);
      } catch (error) {
        console.error('Ошибка при загрузке товаров:', error);
      }
    };

    fetchProducts();
  }, []);

  function displayProducts(products) {
    // Получаем контейнер, в который будут вставляться карточки товаров
    const productList = document.querySelector('.product-list');
  
    // Перебираем массив товаров
    products.forEach(product => {
      // Создаем элемент карточки товара
      const productCard = document.createElement('div');
      productCard.classList.add('product-card');
  
      // Создаем изображение товара
      const productImage = document.createElement('img');
      productImage.src = product.image;
      productImage.alt = product.name;
      productImage.classList.add('product-image');
  
      // Создаем заголовок товара
      const productName = document.createElement('h3');
      productName.textContent = product.name;
  
      // Создаем цену товара
      const productPrice = document.createElement('p');
      productPrice.textContent = `Цена: $${product.price}`;
  
      // Добавляем изображение, заголовок и цену в карточку товара
      productCard.appendChild(productImage);
      productCard.appendChild(productName);
      productCard.appendChild(productPrice);
  
      // Добавляем карточку товара в контейнер
      productList.appendChild(productCard);
    });
  }
  
  // Вызываем функцию для отображения карточек товаров
  displayProducts(media);
  

  return (
    <div className="catalog">
      <Header/>
      <h2>Каталог товаров</h2>
      <div className="product-list">
        {media.map(media => (
              <div key={media.id} className="product-card">
                <img src={media.image} alt={media.name} className="product-image" />
                <h3>{media.name}</h3>
                <p>Цена: ${media.price}</p>
              </div>
            )
          )
        }
      </div>
      <Footer/>
    </div>
  );
};

export default Catalog;
