// pages/Catalog.js
import React, { useContext } from 'react';
import { CartContext } from '../components/CartContext';
import "../App.css";
import Footer from '../components/Footer';
import Header from '../components/Header';

const Catalog = () => {
  const { addToCart } = useContext(CartContext);

  const products = [
    { id: 1, name: 'Ryzen 5 5500 RX 6500XT 4Gb', price: "49 999", image: 'https://static.insales-cdn.com/r/BNe28QLPt4E/rs:fit:800:800:1/plain/images/products/1/3302/832400614/large_1.jpg@webp', description: 'Производитель x64; Процессор AMD Ryzen 5 5500; Видеокарта AMD Radeon RX 6400 4Gb; Материнская плата A520M PRO; Оперативная память DDR4 3200Mhz 16Gb (2*8);' },
    { id: 2, name: 'Товар 2', price: 200, image: 'image2.jpg', description: 'Описание товара 2' },
    // добавьте больше товаров
  ];

  return (
    <div className="catalog">
      <Header />
      <h2 className='CatalogTitle'>Каталог</h2>
      <div className="product-list">
        {products.map((product) => (
          <div key={product.id} className="product-card">
            <img src={product.image} alt={product.name} className="product-image" />
            <h3>{product.name}</h3>
            <p>Цена: {product.price} руб.</p>
            <p className='product-desc'>{product.description}</p>
            <button onClick={() => addToCart(product)}>Купить</button>
          </div>
        ))}
      </div>
      <Footer />
    </div>
  );
};

export default Catalog;
