import axios from 'axios';
import React, { useState } from 'react';
import { useForm } from "react-hook-form";

const TestForm = () => {
    const [inputData, setInputData] = useState({name:'',description:'',url:''});
    const { register, handleSubmit } = useForm();

    const onSubmit = () => {
        axios.post('http://localhost:3001/media', inputData)
        .then(res => {
            alert("Data sent successfully");
        })
        .catch(err => {
            alert("Error sending data");
        });
    };

    const onUpdate = () => {
        axios.put('http://localhost:3001/media', inputData) // Используйте метод put для обновления данных
        .then(res => {
            alert("Data updated successfully");
        })
        .catch(err => {
            alert("Error updating data");
        });
    };

    const handleInputChange = (e, field) => {
        setInputData({ ...inputData, [field]: e.target.value });
    };

    return (
        <div className="flex justify-center lg:h-screen items-center">
            <div className="card w-full md:w-96 items-center shadow-2xl bg-base-100">
                <form onSubmit={handleSubmit(onSubmit)} className="card-body w-full lg:w-96">
                    <div className="form-control">
                        <label className="label">
                            <span className="label-text">Name</span>
                        </label>
                        <input
                            onChange={e => handleInputChange(e, 'name')}
                            type="text" placeholder="name" className="input input-bordered" />
                    </div>

                    <div className="form-control">
                        <label className="label">
                            <span className="label-text">Description</span>
                        </label>
                        <input
                            onChange={e => handleInputChange(e, 'description')}
                            type="text" placeholder="description" className="input input-bordered" />
                    </div>

                    <div className="form-control">
                        <label className="label">
                            <span className="label-text">Image</span>
                        </label>
                        <input
                            onChange={e => handleInputChange(e, 'url')}
                            type="url" placeholder="url image" className="input input-bordered" />
                    </div>

                    <div className="form-control mt-6">
                        <button type="submit" className="btn btn-primary">Send Data</button>
                    </div>
                </form>
            </div>
        </div>
    );
};

export default TestForm;
