// src/App.js
import React from 'react';
import { BrowserRouter as Router, Route, Routes, Switch, Redirect } from 'react-router-dom';
import './App.css';
import Header from './components/Header';
import Cards from './components/Cards';
import InfoCard from './components/InfoCard';
import Footer from './components/Footer';
import Cart from './pages/Cart';
import Catalog from './pages/Catalog';
import FavoritePage from './pages/FavoritePage';
import ProfilePage from './pages/ProfilePage';
import { FavoritesProvider } from './components/FavoritesContext';
import { CartProvider } from './components/CartContext';

const PrivateRoute = ({ component: Component, isLoggedIn, ...rest }) => (
  <Route {...rest} render={(props) =>
    isLoggedIn ? (
      <Component {...props} />
    ) : (
      <Redirect to="/login" />
    )
  } />
);

const [isLoggedIn, setIsLoggedIn] = useState(false); // Состояние для проверки аутентификации


function App() {



  return (
    <CartProvider>
      <FavoritesProvider>
          <Router>
            <div className="App">
              <Routes>
                <Route path="/" element={
                  <>
                    <Header/>
                    <h1>Популярные категории</h1>
                    <Cards />
                    <InfoCard />
                    <Footer/>
                  </>
                } />
                <Route path="/Catalog" element={<Catalog/>} />
                <Route path="/Favorites" element={<FavoritePage/>} />
                <Route path="/Cart" element={<Cart/>} />
                <Route path='/Profile' element={<ProfilePage/>}/>
                <Route path="/login" component={LoginPage} />
                <PrivateRoute path="/profile" component={ProfilePage} isLoggedIn={isLoggedIn} />
                <Redirect exact from="/" to="/profile" />
              </Routes>
            </div>
          </Router>
      </FavoritesProvider>
    </CartProvider>
  );
}

export default App;
