import axios from 'axios';
import React, { useEffect, useState } from 'react';
import { useForm } from "react-hook-form";

function Card() {
    const [data, setData] = useState([]);
    const [selectedItem, setSelectedItem] = useState(null); // Добавляем состояние для выбранного элемента
    const { register, handleSubmit } = useForm();

    useEffect(() => {
        axios.get('http://localhost:3001/media')
        .then(res => setData(res.data))
        .catch(err => console.log(err));
    }, []);

    const updateData = (id) => {
        const selectedItem = data.find(item => item.id === id); // Находим выбранный элемент по id
        setSelectedItem(selectedItem); // Устанавливаем выбранный элемент в состояние
    };

    const onSubmit = (updatedData) => {
        axios.put(`http://localhost:3001/media/${selectedItem.id}`, updatedData) // Используем метод put для обновления данных
        .then(res => {
            alert("Data updated successfully");
            // Дополнительная логика после успешного обновления данных
        })
        .catch(err => {
            alert("Error updating data");
            // Дополнительная логика в случае ошибки обновления данных
        });
    };

    return(
        <div>
            <div className="cards">
                {data.map((d,i) => {
                    return <div className="card" key={i}>
                        <h3 className="card-title">{d.name}</h3>
                        <p className="card-text">{d.description}</p>
                        <img className="card-img-top" src={d.url} />
                        <button onClick={() => updateData(d.id)}>Send to Form</button> {/* Вызов функции обновления данных при нажатии кнопки */}
                    </div>
                })}
            </div>
            {selectedItem && ( // Отображаем форму только если выбран элемент
                <form onSubmit={handleSubmit(onSubmit)}>
                    <input type="text" defaultValue={selectedItem.name} {...register("name")} />
                    <input type="text" defaultValue={selectedItem.description} {...register("description")} />
                    <input type="url" defaultValue={selectedItem.url} {...register("url")} />
                    <button type="submit">Update Data</button>
                </form>
            )}
        </div>
    );
}

export default Card;
