import React, { useState } from 'react';

const ProductList = ({ media }) => {
  const [sortType, setSortType] = useState('');

  const sortedMedia = [...media].sort((a, b) => {
    if (sortType === 'name') {
      return a.name.localeCompare(b.name);
    } else if (sortType === 'price') {
      return a.price - b.price;
    }
    return 0;
  });

  return (
    <div>
      <div className="sort-buttons">
        <button onClick={() => setSortType('name')}>Сортировать по названию</button>
        <button onClick={() => setSortType('price')}>Сортировать по цене</button>
      </div>
      <div className="product-list">
        {sortedMedia.map(media => (
          <div key={media.id} className="product-card">
            <img src={media.image} alt={media.name} className="product-image" />
            <h3>{media.name}</h3>
            <p>Цена: ${media.price}</p>
            <p className='product-desc'>
              Производитель x64; Процессор AMD Ryzen 5 5500; Видеокарта AMD Radeon RX 6400 4Gb; Материнская плата A520M PRO; Оперативная память DDR4 3200Mhz 16Gb (2*8);
            </p>
            <button className="product-buy-btn">Купить</button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default ProductList;
