import React, { useEffect, useState } from 'react';
import axios from 'axios';
import "../App.css";
import Footer from '../components/Footer';
import Header from '../components/Header';

const Catalog = () => {
  const [media, setProducts] = useState([]);
  const [sortType, setSortType] = useState('');
  const [searchTerm, setSearchTerm] = useState('');
  const [suggestions, setSuggestions] = useState([]);

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const response = await axios.get('http://localhost:3001/media');
        setProducts(response.data);
      } catch (error) {
        console.error('Ошибка при загрузке товаров:', error);
      }
    };

    fetchProducts();
  }, []);

  const handleSortChange = (e) => {
    setSortType(e.target.value);
  };

  const handleSearchChange = (e) => {
    const value = e.target.value;
    setSearchTerm(value);

    if (value.length > 1) {
      const filteredSuggestions = media.filter(product =>
        product.name.toLowerCase().includes(value.toLowerCase())
      );
      setSuggestions(filteredSuggestions);
    } else {
      setSuggestions([]);
    }
  };

  const handleSuggestionClick = (name) => {
    setSearchTerm(name);
    setSuggestions([]);
  };

  const sortedMedia = [...media].sort((a, b) => {
    if (sortType === 'name') {
      return a.name.localeCompare(b.name);
    } else if (sortType === 'price') {
      return a.price - b.price;
    }
    return 0;
  });

  const filteredMedia = sortedMedia.filter(product =>
    product.name.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div className="catalog">
      <Header />
      <div className="search-container">
        <input 
          type="text" 
          placeholder="Поиск" 
          value={searchTerm} 
          onChange={handleSearchChange} 
          className="search-input" 
        />
        {suggestions.length > 0 && (
          <ul className="suggestions-list">
            {suggestions.map((suggestion, index) => (
              <li key={index} onClick={() => handleSuggestionClick(suggestion.name)}>
                {suggestion.name}
              </li>
            ))}
          </ul>
        )}
      </div>
      <div className="sort-container">
        <label htmlFor="sort">Сортировать по: </label>
        <select id="sort" value={sortType} onChange={handleSortChange}>
          <option value="">Выбрать</option>
          <option value="name">Названию</option>
          <option value="price">Цене</option>
        </select>
      </div>
      <h2>Каталог товаров</h2>
      <div className="product-list">
        {filteredMedia.map(media => (
          <div key={media.id} className="product-card">
            <img src={media.image} alt={media.name} className="product-image" />
            <h3>{media.name}</h3>
            <p>Цена: {media.price} руб.</p>
            <p className='product-desc'>{media.description}</p>
            <button className="product-buy-btn">Купить</button>
          </div>
        ))}
      </div>
      <Footer />
    </div>
  );
};

export default Catalog;
