// pages/Order.js
import React from 'react';
import { useForm } from 'react-hook-form';
import { useFormspree } from '@formspree/react';
import Recaptcha from 'react-recaptcha';
import "../App.css";
import Header from '../components/Header';
import Footer from '../components/Footer';

const Order = () => {
  const { register, handleSubmit, formState: { errors } } = useForm();
  const [state, handleSubmitToFormspree] = useFormspree("yourFormspreeEndpoint");

  const onSubmit = (data) => {
    // Recaptcha проверка
    window.grecaptcha.execute();
  };

  const verifyCallback = (response) => {
    if (response) {
      handleSubmitToFormspree();
    } else {
      alert('Не удалось подтвердить Recaptcha. Пожалуйста, попробуйте снова.');
    }
  };

  return (
    <div className="order">
      <Header />
      <h2 className='OrderTitle'>Оформление заказа</h2>
      <form onSubmit={handleSubmit(onSubmit)}>
        <div>
          <label>Адрес</label>
          <input {...register("address", { required: true })} />
          {errors.address && <p>Адрес обязателен для заполнения</p>}
        </div>
        <div>
          <label>ФИО</label>
          <input {...register("fullname", { required: true })} />
          {errors.fullname && <p>ФИО обязательно для заполнения</p>}
        </div>
        <div>
          <label>Способ оплаты</label>
          <select {...register("payment", { required: true })}>
            <option value="creditCard">Кредитная карта</option>
            <option value="paypal">PayPal</option>
          </select>
          {errors.payment && <p>Способ оплаты обязателен для заполнения</p>}
        </div>
        <Recaptcha
          sitekey="yourRecaptchaSiteKey"
          render="explicit"
          verifyCallback={verifyCallback}
          onloadCallback={() => console.log("Recaptcha loaded")}
        />
        <button type="submit" disabled={state.submitting}>Подтвердить заказ</button>
        {state.succeeded && <p>Заказ успешно отправлен!</p>}
        {state.errors.length > 0 && <p>Ошибка при отправке заказа. Пожалуйста, попробуйте снова.</p>}
      </form>
      <Footer />
    </div>
  );
};

export default Order;
