import axios from 'axios';
import React, { useEffect, useState } from 'react';
import { useForm } from "react-hook-form";

function Card() {
    const [data, setData] = useState([]);
    const [selectedItem, setSelectedItem] = useState(null);
    const { register, handleSubmit } = useForm();

    useEffect(() => {
        axios.get('http://localhost:3001/media')
        .then(res => setData(res.data))
        .catch(err => console.log(err));
    }, []);

    const updateData = (id) => {
        const selectedItem = data.find(item => item.id === id);
        setSelectedItem(selectedItem);
    };

    const onSubmit = (updatedData) => {
        axios.put(`http://localhost:3001/media/${selectedItem.id}`, updatedData)
        .then(res => {
            alert("Data updated successfully");
        })
        .catch(err => {
            alert("Error updating data");
        });
    };

    const onDelete = (id) => {
        axios.delete(`http://localhost:3001/media/${id}`)
        .then(res => {
            setData(data.filter(item => item.id !== id));
            alert("Data deleted successfully");
        })
        .catch(err => {
            alert("Error deleting data");
        });
    };

    return(
        <div>
            <div className="cards">
                {data.map((d,i) => {
                    return <div className="card" key={i}>
                        <h3 className="card-title">{d.name}</h3>
                        <p className="card-text">{d.description}</p>
                        <img className="card-img-top" src={d.url} />
                        <button onClick={() => updateData(d.id)}>Send to Form</button>
                        <button onClick={() => onDelete(d.id)}>Delete</button>
                    </div>
                })}
            </div>
            {selectedItem && (
                <form onSubmit={handleSubmit(onSubmit)}>
                    <input type="text" defaultValue={selectedItem.name} {...register("name")} />
                    <input type="text" defaultValue={selectedItem.description} {...register("description")} />
                    <input type="url" defaultValue={selectedItem.url} {...register("url")} />
                    <button type="submit">Update Data</button>
                </form>
            )}
        </div>
    );
}

export default Card;
